import React, { Component } from 'react';
import './Header.scss';
class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listMenu: [
        {
          title: 'Home',
          drop: true,
          dropDownMenu: [
            {
              title: 'DropDown 1',
            },
            {
              title: 'DropDown 2'
            }
          ]
        },
        {
          title: 'Manga',
          drop: true,
          dropDownMenu: [
            {
              title: 'DropDown 1',
            },
            {
              title: 'DropDown 2'
            }
          ]
        },
        {
          title: 'Blog',
          drop: false
        },
        {
          title: 'About Us',
          drop: true,
          dropDownMenu: [
            {
              title: 'DropDown 1',
            },
            {
              title: 'DropDown 2'
            }
          ]
        },{
          title: 'Shop',
          drop: true,
          dropDownMenu: [
            {
              title: 'DropDown 1',
            },
            {
              title: 'DropDown 2'
            }
          ]
        }
      ] 
    };
  }
  render() {
    return (
      <div id="header" className="header">
        <div className="flex-header">
          <div>
            <img alt="logo" src={require('../../assets/images/logo-light.png')}/>
          </div>
          <div className="list-menu">
            <ul className="list-menu__parent">
              {this.state.listMenu.map((item, index) => 
                <li key={index}>
                  <span>{item.title}</span>
                  <img className={item.drop ? 'show' : 'hide'} alt="down" src={require('../../assets/images/chevron-arrow-down.png')}/>
                  <ul className="list-menu__child">
                    {item.dropDownMenu.map((item, index) =>
                      <li key={index}>{item.title}}</li>
                    )}
                  </ul>
                </li>)}
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

export default Header;
