import React, { Component } from 'react';
import './Header.scss';
class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listMenu: [
        {
          title: 'Home',
          dropdown: true
        },
        {
          title: 'Manga',
          dropdown: true
        },
        {
          title: 'Blog',
          dropdown: false
        },
        {
          title: 'About Us',
          dropdown: true
        },{
          title: 'Shop',
          dropdown: true,
          icon: true
        }
      ] 
    };
  }
  render() {
    return (
      <div id="header" className="header">
        <div>
          <div>
            <img alt="logo" src={require('../../assets/images/logo-light.png')}/>
          </div>
          <div className="list-menu">
            <ul>
              {this.state.listMenu.map((item, index) => 
                <li key={index}>
                  <span>{item.title}</span>
                  <img className={item.dropdown ? 'show' : 'hide'} alt="down" src={require('../../assets/images/chevron-arrow-down.png')}/>
                </li>)}
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

export default Header;
