import React, { Component } from 'react';
import listMenu from '../../resource/listMenu';
import './MenuMobile.scss';

class MenuMobile extends Component {
  constructor(props) {
    super(props);
    this.state = {
    }
  }

  openSubMenu() {
    console.log('Here')
  }

  handleClickOutside = e => {
    if (!this.myRef.current.contains(e.target)) {
      this.setState({ clickedOutside: true });
    }
  };

  render() {
    return (
      <div className={this.props.active ? '' : 'active'}> 
        <div onClick={this.props.toggleMenu} className="background-dark">
        </div>
        <div className="menu-mobile">
          <div className="account-group">
            <div>Sign in</div>
            <div>Sign up</div>
          </div>
          <div className="menu-mobile__group">
            {
              listMenu.map((item,index) => 
                <div onClick={this.openSubMenu} className="list-menu__item" key={index}>
                  <div className="arrow-icon">
                    <svg onClick={this.buttonToggleSearch} preserveAspectRatio="xMidYMid meet" x="0px" y="0px" viewBox="0 0 373.008 373.008">
                      <path d="M61.792,2.588C64.771,0.864,68.105,0,71.444,0c3.33,0,6.663,0.864,9.655,2.588l230.116,167.2    c5.963,3.445,9.656,9.823,9.656,16.719c0,6.895-3.683,13.272-9.656,16.713L81.099,370.427c-5.972,3.441-13.334,3.441-19.302,0    c-5.973-3.453-9.66-9.833-9.66-16.724V19.305C52.137,12.413,55.818,6.036,61.792,2.588z" fill="#FFFFFF"/>
                    </svg>
                  </div>
                  <div className="list-menu__title">
                    <span>{item.title}</span>
                  </div>
                </div>
              )
            }
          </div>
        </div>
      </div>
    );
  }
}

export default MenuMobile;
